from svg_path import SVGPath
import numpy as np
from typing import Any, cast
import warnings


def generateMask(
    size: np.ndarray[Any, np.dtype[np.int_]],
    svgPath: SVGPath,
    stepPerCmd: float = 0.005,
) -> np.ndarray[Any, np.dtype[np.bool_]]:
    assert isinstance(size, np.ndarray)
    assert isinstance(svgPath, SVGPath)
    assert isinstance(stepPerCmd, float)

    def handle_curve(stPos, ctrlPos1, ctrlPos2, endPos):
        nonlocal mask
        nonlocal cursorPos
        nonlocal resultMask

        prevPos = cursorPos
        visited = np.zeros(size, np.bool_)
        maskIsUsed = False

        t = 0
        while t <= 1:
            cursorPos = stPos*(1-t)**3 + 3*ctrlPos1*t*(1-t)**2 + 3*ctrlPos2*t**2*(1-t) + endPos*t**3
            if slope(cursorPos, prevPos) >= 20 and maskIsUsed:
                maskIsUsed = False
                resultMask ^= mask
                visited = np.zeros(size, np.bool_)
                mask = np.zeros(mask.shape, np.bool_)

            intPos = np.rint(cursorPos).astype(np.int32)
            if 0 <= intPos[1] < size[0] and 0 <= intPos[0] < size[1] and not visited[intPos[1], intPos[0]]:
                maskIsUsed = True
                pIntPos = np.rint(prevPos).astype(np.int32)
                mask[intPos[1]:, min(intPos[0],pIntPos[0]):max(intPos[0],pIntPos[0])] = True
                visited[intPos[1], min(intPos[0],pIntPos[0]):max(intPos[0],pIntPos[0])] = True

            t += stepPerCmd
            prevPos = cursorPos

    def handle_line(stPos, endPos):
        nonlocal mask
        nonlocal cursorPos

        visited = np.zeros(size, np.bool_)
        prevPos = cursorPos

        t = 0
        while t <= 1:
            cursorPos = stPos*(1-t) + endPos*t

            intPos = np.rint(cursorPos).astype(np.int32)
            if 0 <= intPos[1] < size[0] and 0 <= intPos[0] < size[1] and not visited[intPos[1], intPos[0]]:
                pIntPos = np.rint(prevPos).astype(np.int32)
                visited[intPos[1]:, min(intPos[0],pIntPos[0]):max(intPos[0],pIntPos[0])] = True
                mask[intPos[1]:, min(intPos[0],pIntPos[0]):max(intPos[0],pIntPos[0])] = True

            prevPos = cursorPos
            t += stepPerCmd

    resultMask = np.zeros(size, np.bool_)
    cursorPos = np.array([0., 0.])
    originPos = None
    rflPos = None

    for prefix, *positions in svgPath:
        prefix = cast(str, prefix)

        # _show(resultMask, 0)
        # print(prefix, positions)
        mask = np.zeros(size, np.bool_)

        if prefix == "M":
            while positions:
                cursorPos, *positions = positions
                originPos = cursorPos

        elif prefix == "m":
            while positions:
                t, *positions = positions
                cursorPos += t
                originPos = cursorPos

        if prefix == "C":
            while positions:
                stPos = cursorPos
                ctrlPos1, ctrlPos2, endPos, *positions = positions
                rflPos = 2*endPos - ctrlPos2
                handle_curve(stPos, ctrlPos1, ctrlPos2, endPos)

        elif prefix == "c":
            while positions:
                stPos = cursorPos
                ctrlPos1, ctrlPos2, endPos, *positions = positions
                rflPos = 2*(endPos+cursorPos) - (ctrlPos2+cursorPos)
                handle_curve(stPos, ctrlPos1+cursorPos, ctrlPos2+cursorPos, endPos+cursorPos)

        elif prefix == "s":
            while positions:
                stPos = cursorPos
                ctrlPos2, endPos, *positions = positions
                ctrlPos1 = rflPos if rflPos is not None else cursorPos
                rflPos = 2*endPos - ctrlPos2
                handle_curve(stPos, ctrlPos1, ctrlPos2+cursorPos, endPos+cursorPos)

        elif prefix == "S":
            while positions:
                stPos = cursorPos
                ctrlPos2, endPos, *positions = positions
                ctrlPos1 = rflPos if rflPos is not None else cursorPos
                rflPos = 2*endPos - ctrlPos2
                handle_curve(stPos, ctrlPos1, ctrlPos2, endPos)

        elif prefix == "l":
            while positions:
                endPos, *positions = positions
                handle_line(cursorPos, endPos+cursorPos)
                resultMask ^= mask
                mask = np.zeros(size, np.bool_)

        elif prefix == "L":
            while positions:
                endPos, *positions = positions
                handle_line(cursorPos, endPos)
                resultMask ^= mask
                mask = np.zeros(size, np.bool_)

        elif prefix == "v":
            endPos = cursorPos.copy()
            endPos[1] += positions[0]
            handle_line(cursorPos, endPos)

        elif prefix == "V":
            endPos = cursorPos.copy()
            endPos[1] = positions[0]
            handle_line(cursorPos, endPos)

        elif prefix == "h":
            endPos = cursorPos.copy()
            endPos[0] += positions[0]
            handle_line(cursorPos, endPos)

        elif prefix == "H":
            endPos = cursorPos.copy()
            endPos[0] = positions[0]
            handle_line(cursorPos, endPos)

        elif prefix.upper() == "Z":
            t = cursorPos.copy()
            handle_line(cursorPos, originPos)
            cursorPos = t

        elif prefix.upper() not in ["M"]:
            warnings.warn(f"{prefix} command is not implemented")

        if prefix.upper() not in ["C", "S"]:
            rflPos = None

        resultMask ^= mask

    return resultMask


def slope(p1, p2):
    t = p1-p2
    if t[0] == 0:
        return float("inf")
    return t[1]/t[0]


def _show(mask, w=10):
    import cv2
    mask = np.array(mask^1, "uint8") * 255
    cv2.imshow("test", mask)
    cv2.waitKey(w)


